import React, { useRef } from "react";
import { styled } from "styled-components";

const ScrollContainer = styled.div`
	margin: 0;
	padding: 0;
	box-sizing: border-box;
`;

const CardList = styled.ul`
	width: 100%;
	height: 500px;
	padding: 0.5rem;
	overflow-y: scroll;
	background: bisque;
`;

const Card = styled.li`
	width: 25%;
	padding: 0.5rem;
	float: left;
	list-style: none;
`;

const CardImage = styled.Img`
	display: block;
	width: 100%;
	height: 300px;
	object-fit: cover;
`;

const CardText = styled.p`
	height: 40px;
	text-align: center;
	line-height: 40px;
	background: #fff;
	font-size: 0.9rem;
`;

function InfiniteScroll() {
	const observer = new IntersectionObserver((items) => {
		console.log(items);
	});
	const circle = document.getElementById("circle");

	observer.observe(circle);

	return (
		<div>
			<CardList>
				<Card>
					<img
						class="card__image"
						src="images/winter-1.jpeg"
						alt="에스파 윈터"
					/>
					<p class="card__text">Aespa :: Winter 1</p>
				</Card>
				<Card>
					<img
						class="card__image"
						src="images/winter-2.jpeg"
						alt="에스파 윈터"
					/>
					<p class="card__text">Aespa :: Winter 2</p>
				</Card>
				<Card>
					<img
						class="card__image"
						src="images/winter-3.jpeg"
						alt="에스파 윈터"
					/>
					<p class="card__text">Aespa :: Winter 3</p>
				</Card>
				<Card>
					<img
						class="card__image"
						src="images/winter-4.jpeg"
						alt="에스파 윈터"
					/>
					<p class="card__text">Aespa :: Winter 4</p>
				</Card>
				<Card>
					<img
						class="card__image"
						src="images/winter-5.jpeg"
						alt="에스파 윈터"
					/>
					<p class="card__text">Aespa :: Winter 5</p>
				</Card>
				<Card>
					<img
						class="card__image"
						src="images/winter-6.jpeg"
						alt="에스파 윈터"
					/>
					<p class="card__text">Aespa :: Winter 6</p>
				</Card>
				<Card>
					<img
						class="card__image"
						src="images/winter-7.jpeg"
						alt="에스파 윈터"
					/>
					<p class="card__text">Aespa :: Winter 7</p>
				</Card>
				<Card>
					<img
						class="card__image"
						src="images/winter-8.jpeg"
						alt="에스파 윈터"
					/>
					<p class="card__text">Aespa :: Winter 8</p>
				</Card>
			</CardList>
		</div>
	);
}

export default InfiniteScroll;
